  const [pageState, setPageState] = useState("mainPage");

  const [dataArray, setDataArray] = useState(null);

  let display;
  let pageHeight = "100%";

  function updatePage(page) {
    setPageState(page);
  }

  if (pageState === "mainPage") {
    display =[<Splash subjectPage="#subjectPage"/>, <Subjects subjectPage="#subjectPage" changePage={updatePage}/>];
    pageHeight = "200%";
  }

  return (
    <div className={styles.App}>
      <div id={pageState} style={{height: {pageHeight}}}>
        {display}
      </div>
    </div>

  );
}

export default App;
